import React from 'react';
import TemperatureChart from './TemperatureChart';
import AskPanel from './AskPanel';

const App: React.FC = () => {
    return (
        <div
            className="App"
            style={{
                fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
                maxWidth: 900,
                margin: '2rem auto',
                padding: '0 1rem',
                color: '#333',
            }}
        >
            <header
                style={{
                    backgroundColor: '#005f73',
                    padding: '1.5rem 1rem',
                    borderRadius: 8,
                    color: 'white',
                    textAlign: 'center',
                    boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                    marginBottom: '2rem',
                }}
            >
                <h1 style={{ margin: 0, fontWeight: 700, fontSize: '2.2rem' }}>
                    Temperature Monitoring System
                </h1>
                <p style={{ marginTop: '0.5rem', fontWeight: 400, fontSize: '1rem', opacity: 0.8 }}>
                    Real-time temperature monitoring and AI assistant
                </p>
            </header>
            <main
                style={{
                    display: 'flex',
                    flexDirection: 'column',
                    gap: '3rem',
                }}
            >
                <TemperatureChart />
                <AskPanel />
            </main>
        </div>
    );
};

export default App;
