import React from 'react';
import TemperatureChart from './TemperatureChart';
import AskPanel from './AskPanel';

const App: React.FC = () => {
    return (
        <div
            style={{
                fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
                maxWidth: 900,
                margin: '2rem auto',
                padding: '0 1rem',
                color: '#333',
                height: '90vh',           // 让整体高度占据视口大部分
                display: 'flex',
                flexDirection: 'column',
            }}
        >
            <header
                style={{
                    backgroundColor: '#005f73',
                    padding: '1rem',
                    borderRadius: 8,
                    color: 'white',
                    textAlign: 'center',
                    boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                    flex: '0 0 auto',
                    fontSize: 'clamp(1.5rem, 3vw, 2.5rem)',  // 标题字体大小随视口变化
                    fontWeight: 700,
                }}
            >
                Temperature Monitoring System
            </header>
            <main
                style={{
                    flex: 1,
                    display: 'flex',
                    flexDirection: 'column',
                    gap: '1.5rem',
                    marginTop: '1rem',
                    overflow: 'auto',
                }}
            >
                <section style={{ flex: 4, minHeight: 0 }}>
                    <TemperatureChart />
                </section>
                <section style={{ flex: 1, minHeight: 0 }}>
                    <AskPanel />
                </section>
            </main>
        </div>
    );
};

export default App;
